        // 
        msg.getToRecipients().add("xx@gmail.com");
        // 
        msg.getCcRecipients().add("xx2@gmail.com");
        // 
        msg.getBccRecipients().add("xx3@gmail.com");
        // 
        msg.getAttachments().addFileAttachment("/Users/xx/Downloads/demo.text");
        msg.send();
    }
}

