```java

import java.util.HashMap;
import java.util.Map;

public class LRUCache {

    /**
     * 
     */
    Node tail;
    /**
     * 
     */
    Node head;
    Map<String, Node> map = null;
    private int capacity = 0; // 
    public LRUCache(int capacity) {
        this.capacity = capacity;
        map = new HashMap<>();
    }

    public String get(String key) {
        Node node = map.get(key);
        if (node == null) {
            return "";
        }
        // 
        removeNode(node);
        //  node 
        moveToHead(node);
        // 
        return node.value;
    }

    public void put(String key, String value) {

        if (map.containsKey(key)) {
            //  key key 
            Node node = map.get(key);
            // 
            removeNode(node);
            //  node 
            moveToHead(node);
        } else {
            // 
            if (map.size() >= capacity) {
                //  
                map.remove(tail.key);
                removeNode(tail);
            }
            //   
            Node node = new Node(key, value);
            moveToHead(node);
            map.put(key, node);

        }
    }

    /**
     * 
     * @param n
     */
    private void removeNode(Node n) {
        if (n.prev != null) {
            n.prev.next = n.next;
        } else {
            head = n.next;
        }
        if (n.next != null) {
            n.next.prev = n.prev;
        } else {
            tail = n.prev;
        }
    }

    /**
     * 
     * @param n
     */
    private void moveToHead(Node n) {
        final Node f = head;
        head = n;
        if (f != null) {
            f.prev = n;
            head.next = f;
        }
        if (tail == null) {
            tail = f;
        }
    }
}
```
