Node<E> node(int index) {
    // assert isElementIndex(index);
    // size >> 1  size/2 LinkedList
    if (index < (size >> 1)) {
        Node<E> x = first; // 
        for (int i = 0; i < index; i++)
            x = x.next;
        return x;
    } else {
        Node<E> x = last;
        for (int i = size - 1; i > index; i--)
            x = x.prev;
        return x;
    }
}
