``` java
public boolean addAll(int index, Collection<? extends E> c) {
    // index [0, size]
    checkPositionIndex(index);

    Object[] a = c.toArray();
    int numNew = a.length; // 
    if (numNew == 0)
        return false;

    Node<E> pred, succ; // pred: index, index
    // 
    if (index == size) {
        succ = null;
        pred = last;
    } else {
        succ = node(index); // indexsucc
        pred = succ.prev; // succ pred
    }

    // 
    for (Object o : a) {
        @SuppressWarnings("unchecked") E e = (E) o;
         //  node
        Node<E> newNode = new Node<>(pred, e, null);
        if (pred == null) // 
            first = newNode;
        else
            pred.next = newNode;
        pred = newNode;
    }

    // 
    if (succ == null) {
        last = pred; // 
    } else { // 
        pred.next = succ; // 
        succ.prev = pred; // 
    }

    size += numNew; // LinkedList
    modCount++; // 
    return true;
}
