E unlink(Node<E> x) {
    // assert x != null;
    // x 
    final E element = x.item; // 
    final Node<E> next = x.next; // x
    final Node<E> prev = x.prev; // x
    // 
    if (prev == null) {
        // LinkedListfrist x
        first = next;
    } else {
        // x x
        prev.next = next;
        x.prev = null; // xnullgc
    }

    if (next == null) {
        last = prev; // xLinkedListx
    } else {
        next.prev = prev; // x x
        x.next = null; // xnullgc
    }

    x.item = null; // xnullgc
    size--; //  -1
    modCount++;
    return element;
}
