        prev.next = next;
        x.prev = null; // xnullgc
    }

    if (next == null) {
        last = prev; // xLinkedListx
    } else {
        next.prev = prev; // x x
        x.next = null; // xnullgc
    }

    x.item = null; // xnullgc
    size--; //  -1
    modCount++;
    return element;
}
