final TreeNode<K,V> find(int h, Object k, Class<?> kc) {
	TreeNode<K,V> p = this;
	do {
		// ph: phashpk:pkey
		int ph, dir; K pk;
		// pl: p prp
		TreeNode<K,V> pl = p.left, pr = p.right, q;
		if ((ph = p.hash) > h)
			// phash hhgethashp
			p = pl;
		else if (ph < h)
			// phash hhgethashp
			p = pr;
		else if ((pk = p.key) == k || (k != null && k.equals(pk)))
			// p.hash==h,key
			return p;
		else if (pl == null)
			// p
			p = pr;
		else if (pr == null)
			// p
			p = pl;
		// kcequivalentClassForkey
		// comparableClassForclass C implements Cormparable <C>kClassnull
		// compareComparables: pkkckk.compareTopk0
		else if ((kc != null ||
				  (kc = comparableClassFor(k)) != null) &&
				 (dir = compareComparables(kc, k, pk)) != 0)
			p = (dir < 0) ? pl : pr;
		else if ((q = pr.find(h, k, kc)) != null)
			return q;
		else
			p = pl;
	} while (p != null);
	return null;
}
