public V put(K key, V value) {
	Entry<K,V> t = root;
	if (t == null) {
		// keycompare
		compare(key, key); // type (and possibly null) check
		// new  entry
		root = new Entry<>(key, value, null);
		size = 1;
		modCount++; // 
		return null;
	}
	int cmp;
	Entry<K,V> parent;
	// split comparator and comparable paths
	Comparator<? super K> cpr = comparator;
	if (cpr != null) {
		// value
		do {
			parent = t;
			cmp = cpr.compare(key, t.key); //  key  t.key
			if (cmp < 0)
				t = t.left;
			else if (cmp > 0)
				t = t.right;
			else
				return t.setValue(value); // cmp=0value
		} while (t != null);
	}
	else {
		if (key == null) // keynull
			throw new NullPointerException();
		@SuppressWarnings("unchecked")
			Comparable<? super K> k = (Comparable<? super K>) key;
		// value
		do {
			parent = t;
			cmp = k.compareTo(t.key);
			if (cmp < 0)
				t = t.left;
			else if (cmp > 0)
				t = t.right;
			else
				return t.setValue(value);
		} while (t != null);
	}
	//  cmp != 0keyentry eeparent
	Entry<K,V> e = new Entry<>(key, value, parent);
	if (cmp < 0)
		parent.left = e;
	else
		parent.right = e;
	fixAfterInsertion(e);
	size++;
	modCount++;
	return null;
}
