final V replaceNode(Object key, V value, Object cv) {
    // keyhashCodehash
    int hash = spread(key.hashCode());
    // table
    for (Node<K,V>[] tab = table;;) {
        Node<K,V> f; int n, i, fh;
        // tabtabAtnode
        if (tab == null || (n = tab.length) == 0 ||
            (f = tabAt(tab, i = (n - 1) & hash)) == null)
            break;
        //hash-1tablehelpTransfer
        else if ((fh = f.hash) == MOVED)
            tab = helpTransfer(tab, f);
        else {
            V oldVal = null;
            boolean validated = false;
            synchronized (f) { // 
                if (tabAt(tab, i) == f) {
                    if (fh >= 0) {
                        validated = true;
                        // key
                        for (Node<K,V> e = f, pred = null;;) {
                            K ek;
                            if (e.hash == hash &&
                                ((ek = e.key) == key ||
                                 (ek != null && key.equals(ek)))) { // ekey
                                V ev = e.val;
                                if (cv == null || cv == ev ||
                                    (ev != null && cv.equals(ev))) { // valuenullvaluecv
                                    oldVal = ev;
                                    if (value != null) // valuenullevalue
                                        e.val = value;
                                    else if (pred != null) // nulle
                                        pred.next = e.next;
                                    else
                                        setTabAt(tab, i, e.next); // nullnextnext
                                }
                                break;
                            }
                            pred = e;
                            if ((e = e.next) == null) // key
                                break;
                        }
                    }
                    // 
                    else if (f instanceof TreeBin) {
                        validated = true;
                        TreeBin<K,V> t = (TreeBin<K,V>)f;
                        TreeNode<K,V> r, p;
                        // findTreeNodekey
                        if ((r = t.root) != null &&
                            (p = r.findTreeNode(hash, key, null)) != null) {
                            V pv = p.val;
                            if (cv == null || cv == pv ||
                                (pv != null && cv.equals(pv))) {
                                oldVal = pv;
                                if (value != null)
                                    p.val = value;
                                //removeTreeNodetrue
                                else if (t.removeTreeNode(p))
                                    setTabAt(tab, i, untreeify(t.first));
                            }
                        }
                    }
                }
            }
            if (validated) {
                if (oldVal != null) {
                    if (value == null)
                        addCount(-1L, -1);
                    return oldVal;
                }
                break;
            }
        }
    }
    return null;
}
